mapper.select:
SELECT board_id, subject, title, writer, write_date, is_answered FROM ( SELECT board_id, subject, title, writer, write_date, is_answered, row_number() over(order by board_id DESC) as rNum FROM qna_board WHERE writer_id = #{userId}
AND title LIKE '%' || #{keyword} || '%'
AND content LIKE '%' || #{keyword} || '%'
AND subject LIKE '%' || #{keyword} || '%'
AND (title LIKE '%' || #{keyword} || '%' OR content LIKE '%' || #{keyword} || '%')
) sub WHERE rNum between #{criteria.startRow} AND #{criteria.endRow} ORDER BY board_id DESC
