mapper.select:
SELECT board_id, subject, title, content, writer, write_date, view_cnt, is_answered, file_path
FROM qna_board
WHERE board_id = #{board_id}

mapper.update:
UPDATE qna_board
SET view_cnt = view_cnt + 1
WHERE board_id = #{board_id}

mapper.delete:
UPDATE qna_board
SET is_deleted = 'Y'
WHERE board_id = #{board_id}
